INSERT INTO `tpcloud_cms_plugin` VALUES (4, 'content-box', 'Tại sao chọn chúng tôi', 'Why choose us', 'uploads/admin/blog-category/20191019/0157146043519102019_page.jpg', '<p>Tpcloud là nhà cung cấp Cloud ĐẦU TIÊN và DUY NHẤT tại Việt Nam được VMware công nhận là Professional Cloud Provider - nhà cung cấp công nghệ đám mây chuyên nghiệp.\r\n</p><p>\r\nĐược VMware cấp chứng nhận VMware Cloud Certified bởi vượt qua các yêu cầu về hoạch định và thiết kế hệ thống của Vmware - VMware Validated Design (VVD)</p>', '<p>Tpcloud is the FIRST and ONLY Cloud provider in Vietnam recognized by VMware as Professional Cloud Provider - a professional cloud technology provider. </p><p>Being VMware certified by VMware Cloud Certified by exceeding VMware\'s planning and system design requirements - VMware Validated Design (VVD)<br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 18:14:50', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (5, 'item-box', 'Sử dụng linh hoạt', 'Flexible use', 'icon icon-use', 'Không lo về các truy cập hạn chế, bạn có quyền tuỳ chỉnh máy chủ ảo theo nhu cầu của chính bạn và chỉ trả phí cho các tài nguyên bạn sử dụng.', '<p>Without worrying about restricted access, you have the right to customize the virtual server according to your own needs and pay only for the resources you use.<br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 18:15:59', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (6, 'item-box', 'Kiểm soát chi phí', NULL, 'icon icon-use', 'Đừng lo về một \"khoản chi bất ngờ\" từ hoá đơn. Tpcloud luôn hiển thị chi phí và các chương trình ưu đãi thường xuyên. Chắc chắn mọi thứ đều được thông báo đến bạn và trong tầm kiểm soát.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (9, 'item-box', 'Thao tác quen thuộc trên VMware', NULL, 'icon icon-use', 'Dữ liệu của bạn sẽ được lưu trữ an toàn tại Tpcloud được chứng nhận cấp doanh nghiệp bởi VMware. Và, mọi trải nghiệm của bạn sẽ giống như với VMware bạn tin tưởng.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (10, 'item-box', 'Chủ quyền dữ liệu', NULL, 'icon icon-use', 'Tpcloud đảm bảo dữ liệu của bạn chỉ hiển thị trong khu vực địa lý bạn tuỳ chọn.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (11, 'item-box', 'Quản lý API hoặc UI', NULL, 'icon icon-use', 'Với cổng thông tin của Tpcloud, cho phép bạn quản lý dữ liệu Cloud và toàn bộ hạ tầng bằng API REST của vCloud Manager bằng mã code.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (12, 'item-box', 'Kết nối mạng dễ dàng', NULL, 'icon icon-use', 'TPCloud cung cấp các công cụ mạng và tường lửa tiên tiến nhất hiện có ở mọi nơi bao gồm VMware NSX, IPSEC, MPLS, VLAN, BGP và hơn thế nữa.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (13, 'item-box', 'Tích hợp nhiều đám mây', NULL, 'icon icon-use', 'Chúng tôi là một phần của giải pháp đa đám mây của bạn. Chúng tôi đã giúp bạn dễ dàng kết nối trực tiếp với AWS, Azure và Google qua trung tâm dữ liệu DCI', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (14, 'footer-box', 'Dùng thử miễn phí', 'Try it free', 'uploads/admin/blog-category/20191020/5157154131320102019_page.png', 'Dùng thử TPCloud Server trong 3 ngày với cấu hình 2 Core - 2 GB RAM - 100GB HDD + 60GB SSD\r\nDùng thử TP CDN miễn phí 100GB dung lượng trong vòng 30 ngày', '<p>Try TPCloud Server for 3 days with 2 Core configuration - 2 GB RAM - 100GB HDD + 60GB SSD Try TP CDN free 100GB capacity for 30 days<br></p>', NULL, NULL, 11, 'Dùng thử ngay', NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 03:15:14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (15, 'item-box', 'Công nghệ đám mây hàng đầu', NULL, 'icon icon-use', 'Việc tận dụng tối đa nền tảng của VMware, cho phép chúng tôi cung cấp đến khách hàng mức độ dịch vụ doanh nghiệp với các tính năng như vMotion, HA, Fault Tolerance và DRS.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (16, 'item-box', 'Hạ tầng thiết bị doanh nghiệp\r\n', NULL, 'icon icon-use', 'Tpcloud sử dụng thiết bị đồng bộ, đạt tiêu chuẩn hàng đầu từ DELL/ EMC và Jupiter, tạo ra một môi trường mở rộng cho nền tảng doanh nghiệp.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (17, 'item-box', 'Tự động hóa', NULL, 'icon icon-use', 'Tpcloud cung cấp dịch vụ đám mây đề cao tự động hoá, cho khách hàng quyền kiểm soát tối cao và tiết kiệm thời gian, nhân lực tốt nhất có thể.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (18, 'content-box', 'Chúng tôi là ai?', 'Who are we?', 'uploads/admin/blog-category/20191019/1157146041519102019_page.jpg', '<p>TPcloud là dịch vụ cloud, máy chủ ảo sử dụng hạ tầng công nghệ VMware và thuộc sở hữu của TPcoms - Công ty Cổ Phần Công Nghệ Tiên Phát, nhà cung cấp các dịch vụ viễn thông và CNTT thành lập năm 2012, với hạ tầng viễn thông rộng khắp lãnh thổ Việt Nam. </p><p>Năm 2019, TPComs trở thành doanh nghiệp Việt Nam đầu tiên ký kết với VMWare - nhà cung ứng dịch vụ cloud hangd đầu thế giới, đưa hạ tầng tích hợp gồm: máy chủ, lữu trữ, kết nối và bảo mật (computer, storage, networking, và security), hỗ trợ tối ưu điều hành và quản lý tự động hệ thống công nghệ thông tin của doanh nghiệp.</p>', '<p>TPcloud is a cloud service, a virtual server using VMware technology infrastructure and owned by TPcoms - Tien Phat Technology Joint Stock Company, a provider of telecommunications and IT services established in 2012, with infrastructure telecommunications throughout the territory of Vietnam. </p><p>In 2019, TPComs became the first Vietnamese enterprise to sign with VMWare - the world\'s leading cloud service provider, bringing integrated infrastructure including server, storage, connection and security (computer, storage). , networking, and security), optimally supporting the management and automated management of enterprise IT systems.<br></p>', NULL, NULL, 1, '', NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:47:01', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (19, 'footer-box', 'Đối tác của chúng tôi', 'Our partner', 'uploads/admin/blog-category/20191019/4157151019319102019_page.jpg', 'Tpcloud và đối tác tạo nên một hệ sinh thái đám mây toàn cầu, cung cấp giải pháp cloud hữu hiệu. Bạn là nhà cung cấp MSP, JSV, VAR, IT hay SaaS, hãy tham gia cùng chúng tôi.', '<p>Tpcloud and its partners create a global cloud ecosystem, providing effective cloud solutions. You are a supplier of MSP, JSV, VAR, IT or SaaS, please join us.<br></p>', NULL, NULL, 1, 'Tìm hiểu thêm', NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 18:37:39', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (20, 'content-box', 'Công nghệ đám mây hàng đầu', 'Leading cloud technology', 'uploads/admin/blog-category/20191019/2157146047419102019_page.jpg', 'Việc tận dụng tối đa nền tảng của VMware, cho phép chúng tôi cung cấp đến khách hàng mức độ dịch vụ doanh nghiệp với các tính năng như vMotion, HA, Fault Tolerance và DRS.', '<p>Making the most of VMware\'s platform allows us to provide our customers with a level of enterprise service with features like vMotion, HA, Fault Tolerance and DRS.<br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:47:57', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (21, 'content-box', 'Hạ tầng thiết bị doanh nghiệp', 'Enterprise equipment infrastructure', 'uploads/admin/blog-category/20191019/1157146065119102019_page.jpg', 'Tpcloud sử dụng thiết bị đồng bộ, đạt tiêu chuẩn hàng đầu từ DELL/ EMC và Jupiter, tạo ra một môi trường mở rộng cho nền tảng doanh nghiệp.', '<p>Tpcloud uses synchronous equipment, meets the leading standards from DELL / EMC and Jupiter, creating an expanded environment for the enterprise platform.<br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:50:52', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (22, 'content-box', 'Tự động hóa', 'Automation', 'uploads/admin/blog-category/20191019/5157146066319102019_page.jpg', 'Tpcloud cung cấp dịch vụ đám mây đề cao tự động hoá, cho khách hàng quyền kiểm soát tối cao và tiết kiệm thời gian, nhân lực tốt nhất có thể.', '<p>Tpcloud provides automation-enhanced cloud services, giving customers the ultimate control and saving the best time and manpower possible.<br></p>', NULL, NULL, 4, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:51:04', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (23, 'content-box', 'Đơn giản hóa trải nghiệm đám mây của bạn', 'Simplify your cloud experience', 'uploads/admin/blog-category/20191019/0157146077019102019_page.jpg', 'Dịch vụ Cloud Server / Public Cloud / Cloud VPS cung cấp máy chủ ảo hoạt động trên hệ thống cloud duy nhất tại Việt Nam được vmware kiểm chứng, tuân thủ theo các quy định nghiêm ngặt của VMW toàn cầu về độ an toàn và tính ổn định cao:', '<p>Cloud Server / Public Cloud / Cloud VPS service provides a virtual server operating on the only cloud system in Vietnam verified by vmware, in compliance with strict VMW global regulations on safety and stability. high rating:<br></p>', '<p><br></p>', NULL, 6, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:52:56', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (24, 'content-box', 'Quản lý thông minh', 'Smart management', 'uploads/admin/blog-category/20191019/1157146081519102019_page.jpg', 'Việc quản lý và thao tác của bạn sẽ được tối giản và tiết kiệm thời gian. Doanh nghiệp có quyền truy cập vào VMware® vCloud để mở rộng các tính năng quản lý nâng cao', '<p>Your management and operation will be minimal and save time. Businesses have access to VMware® vCloud to expand advanced management features<br></p>', NULL, NULL, 8, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:53:37', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (25, 'item-box', 'Tích hợp VMware', 'VMware Integrated', 'icon icon-use', 'Hệ thống được triển khai trên hạ tầng công nghệ của VMware dưới sự điều hành và giám sát bởi các chuyên gia VMware cùng đôi ngũ kỹ sư được đào tạo và chứng nhận của Vmware.', '<p>All \"click\" you make on you Cloud is provided by Tpcloud with VMware system intergrated and certified vy VMware standard: convenience and secured.<br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 16:24:47', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
