INSERT INTO `tpcloud_cms_plugin` VALUES (30, 'item-box', 'Chi phí hợp lý', NULL, 'icon icon-use', 'Tiết kiệm chi phí thiết lập ban đầu, chi trả chi phí hợp lý hàng tháng. Đặc biệt, doanh nghiệp có thể thay đổi mức chi trả theo như cầu sử dung thực tế.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (31, 'content-box', 'Dữ liệu đám mây an toàn', 'Secure cloud data', 'uploads/admin/blog-category/20191019/3157146091619102019_page.jpg', 'Tpcloud cung cấp giải pháp dữ liệu đám mây doanh nghiệp an toàn, bảo mật toàn diện và đảm bảo hiệu suất lưu trữ tối đa.', '<p>Tpcloud provides secure, comprehensive data cloud solutions for businesses and ensures maximum storage performance.<br></p>', NULL, NULL, 8, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:55:22', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (32, 'item-box', 'Quản lý dữ liệu theo nhu cầu', NULL, 'icon icon-use', 'Bạn có quyền truỳ cập đầy đủ tài nguyên và công cụ để sử dụng, quản lý dữ liệu đám mây riêng tư hoặc bảo mật.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
