INSERT INTO `tpcloud_cms_plugin` VALUES (34, 'item-box', 'Mạng & đồng địa điểm', NULL, 'icon icon-use', 'Thiết lập kết nối Ethernet trực tiếp, với lưu lượng miễn phí giữa các văn phòng của bạn và trung tâm dữ liệu của chúng tôi', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (35, 'content-box', 'VMware - nhà cung cấp Cloud hàng đầu thế giới', 'VMware - the leading Cloud provider', 'uploads/admin/blog-category/20191019/0157146095319102019_page.jpg', 'VMware là \"ông lớn\" cung cấp giải pháp đáp ứng đòi hỏi công nghệ phức tạp nhất. Dó là lý do các doanh nghiệp trên toàn thế giới tin tưởng VMware. Và lượng khách hàng trung thành của Vmware hiện nay là 500.000 doang nghiệp trên toàn cầu.', '<p>VMware is the \"big\" provider of solutions that meet the most complex technology requirements. That\'s why businesses all over the world trust VMware. And Vmware\'s loyal customer base is 500,000 businesses worldwide.<br></p>', NULL, NULL, 9, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:55:58', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (36, 'content-box', 'Dịch vụ đa dang và luôn mở rộng', 'The service is multifaceted and always expanding', 'uploads/admin/blog-category/20191019/1157146098919102019_page.jpg', 'Chưa bao giờ thất vọng với hệ thống cũng như chất lượng dịch vụ Vmware cung cấp. Từ dịch vụ trung tâm dữ liệu đến hạ tầng đám mây, VMware luôn cải tiến và phát huy phương châm cốt lõi, trở thành biểu tượng công nghệ thông tin hiện nay.', '<p>Never been disappointed with the system and the quality of services VMware provides. From data center services to cloud infrastructure, VMware is constantly improving and promoting its core motto, becoming the current IT symbol.<br></p>', NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:56:32', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (37, 'content-box', 'Một \"cái bắt tay lớn\" giữa Tpcoms và VMware', 'A \"big handshake\" between Tpcoms and VMware', 'uploads/admin/blog-category/20191019/2157146101919102019_page.jpg', 'VMware và Tpcoms trở thành đối tác chính thức, đưa công nghệ đám mây Cloud hàng đầu thế giới phục vụ nhu cầu đang ngày càng tăng tại Việt Nam.', '<p>VMware and Tpcoms become official partners, bringing the world\'s leading cloud technology to meet the growing demand in Vietnam.<br></p>', NULL, NULL, 12, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 04:57:03', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (43, 'slider-box', 'Đơn giản hóa trải nghiệm đám mây của bạn test', 'Minh heo test', 'uploads/admin/blog-category/20191019/4157146193419102019_page.png', '<p>Đưa trải nghiệm dịch vụ cloud - đám mây của bạn lê<span style=\"background-color: rgb(255, 255, 0);\">n tầm cao mới với các tiện ích mở rộng và sẵn sà</span>ng kích hoạt đơn giản, nhanh chóng.<br></p>', NULL, NULL, NULL, 100, 'Minh heo', NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 08:41:08', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (44, 'slider-box', 'Đơn giản hóa trải nghiệm đám mây của bạn 1', 'Minh heo', 'uploads/admin/blog-category/20191019/4157146209719102019_page.png', '<p>Đưa trải nghiệm dịch vụ cloud - đám mây của bạn lên tầm cao mới với các tiện ích mở rộng và sẵn sàng kích hoạt đơn giản, nhanh chóng.<br></p>', NULL, '<p><br></p>', NULL, 99, 'Minh heo', NULL, NULL, NULL, 'left', 1, 0, NULL, '2019-10-19 05:15:39', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (47, 'item-box', 'Khoản chi rõ ràng', 'Clear expenditure', 'icon icon-use', 'Tất cả bạn nhận được từ Tpcloud sẽ luôn đơn giản, dễ hiểu. Bảng kê chi phí của bạn sẽ luôn cụ thể rành mạch từng dịch vụ.', 'All you receive from Tpcloud will be simple and easy to understand. Your expense list will always be specific for each service.', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (48, 'item-box', 'Chuyên môn toàn cầu', 'Global expertise', 'icon icon-use', 'Tpcloud cung cấp dịch vụ đám mây tại Việt Nam. Và, đội ngũ chuyên gia của chúng tôi có thể hỗ trợ bạn ở phạm vi toàn cầu.', 'Tpcloud provides cloud services in Vietnam. And, our team of experts can assist you on a global scale.', NULL, NULL, 4, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (49, 'item-box', 'Phần cứng doanh nghiệp', 'Business hardware', 'icon icon-use', 'Môi trường của chúng tôi là đại diện của một nền tảng doanh nghiệp mở rộng. Mọi khu vực đều được thiết kế giống nhau và sử dụng các thiết bị hàng đầu trong ngành từ DELL / EMC và Juniper.', 'Our environment is representative of an expanding business platform. Every area is designed the same and uses industry-leading equipment from DELL / EMC and Juniper.', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (50, 'item-box', 'Tự động hóa', 'Automation', 'icon icon-use', 'Tpcloud cung cấp dịch vụ đám mây đề cao tự động hoá, cho khách hàng quyền kiểm soát tối cao, tiết kiệm thời gian, nhân lực tốt nhất có thể.', 'Tpcloud offers automation cloud service, giving customers the ultimate control, saving time and manpower as best as they can.', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (51, 'content-box', 'Khởi tạo dữ liệu riêng tư của bạn trong vài phút', 'Initialize your private data in minutes', 'uploads/admin/blog-category/20191019/4157151138319102019_page.jpg', NULL, NULL, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 18:56:27', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (52, 'content-box', 'Kiểm soát VM tối ưu', 'Optimal VM control', 'uploads/admin/blog-category/20191019/6157151148719102019_page.jpg', '<p><span style=\"color: rgb(68, 68, 68); font-family: \"Open Sans\"; font-size: 16px; background-color: rgb(252, 252, 252);\">Tpcloud tối giản cổng thông tin triển khai, giúp hoạt động của bạn thực thi trong vài phút. Đồng thời VM chuyên dụng cho bạn truy cập bảng điều khiển đầy đủ thông tin về hiệu suất và lưu lượng truy cập của bạn.</span><br></p>', '<p>Tpcloud simplifies the deployment portal, helping your operations run in minutes. At the same time, the dedicated VM gives you access to the dashboard full of information about your performance and traffic.<br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 18:58:08', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (53, 'item-box', 'Duy trì lợi nhuận đều đặn', 'Maintain steady profits', 'icon icon-use', 'Với lượng khách hàng cố định và gia tăng đều đặn, Tpcloud cùng các tiện ích sẽ đem lại lợi nhuận hấp dẫn cho việc kinh doanh của bạn.', 'With a fixed number of customers and a steady increase, Tpcloud and utilities will bring attractive profits for your business.', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (54, 'item-box', 'Dễ dàng bắt đầu và vận hành', 'Easy to get started and operate', 'icon icon-use', 'Tpcloud đã có sẵn những tài nguyên cần thiết để bạn bắt công việc kinh doanh. Tiện ích cao, thông tin đầy đủ và luôn cập nhật sẽ giúp khách hàng tìm đến bạn dễ dàng.', 'Tpcloud already has the resources you need to start your business. High utilities, complete information and always up to date will help customers find you easily.', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (55, 'item-box', 'Chương trình affiliate đặc biệt', 'Special affiliate program', 'icon icon-use', 'Tpcloud sẽ luôn có các chương trình đặc biệt để bạn \"đồng quảng bá\", từ đó chia sẻ khách hàng và cơ hội kinh doanh tiềm năng.', 'Tpcloud will always have special programs for you to \"co-promote\", from which to share customers and potential business opportunities.', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (56, 'footer-box', 'Trở thành đại lý', 'Become an agent', 'uploads/admin/blog-category/20191019/3157151343919102019_page.png', '<p><span style=\"color: rgb(68, 68, 68); font-family: \"Open Sans\"; font-size: 16px; background-color: rgb(248, 249, 250);\">Vui lòng liên hệ với chúng tôi để biết thêm thông tin, điều kiện để trở thành đại lý của TPCloud</span><br></p>', '<p>Please contact us for more information, conditions to become an agent of TPCloud<br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 19:30:40', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (57, 'item-box', 'Đơn giản và hiệu quả hơn', 'Simple and more effective', 'icon icon-use', 'Khởi tạo máy chủ ảo linh hoạt, tự cấu hình,quản trị và giám sát hoạt động máy chủ ảo giúp tối ưu hóa chi phí đầu tư.', 'Create a virtual server that is flexible, self-configuring, managing and monitoring virtual server operations to help optimize investment costs.', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-19 19:36:49', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (58, 'item-box', 'Tăng cường an toàn và bảo mật', 'Increase safety and security', 'icon icon-use', 'Dữ liệu lưu trữ được bảo vệ bởi các dịch vụ vFirewall và Loadbalancing trên hạ tầng cơ sở vật lý dự phòng.', 'Data storage is protected by vFirewall and Loadbalancing services on redundant physical infrastructure.', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (59, 'item-box', 'Xây dựng hệ thống mạng riêng', 'Building a private network', 'icon icon-use', 'Tạo và quản lý mạng nội bộ, chia sẻ mạng hoặc kết nối internet nhiều cấp. Tuỳ chọn kết nối với cơ sở hạ tầng ảo hoá khác xây dựng Hybrid Cloud.', 'With Tpcloud solutions, you can build an internal network for your compay for your team members can connect and engage to their task and to each others.', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 16:32:18', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (60, 'item-box', 'An toàn, bảo mật', 'Safe, confidential', 'icon icon-use', 'Mối quan tâm hàng đầu của doanh nghiệp về an toàn lưu trữ dữ liệu sẽ được hệ thống của Tpcloud đảm bảo bảo mật cao nhất nhờ hạ tầng VMware và đối tác.', 'The top concern of enterprises about data storage safety will be guaranteed by Tpcloud system thanks to VMware infrastructure and partners.', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (61, 'item-box', 'Mở rộng cấu hình linh hoạt', 'Flexible configuration extension', 'icon icon-use', 'Hệ thống quản lý tự động giúp khởi tạo dịch vụ nhanh và mở rộng cấu hình khống giới hạn', 'Automated management system helps to quickly create services and expand unlimited configuration', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (62, 'item-box', 'Triển khai nhanh chóng', 'Deploy quickly', 'icon icon-use', 'Chỉ vài thao tác click, việc lưu trữ dữ liệu của bạn được khởi tạo tự động, triển khai nhanh chóng và thuận tiện.', 'With just a few clicks, your data storage is automatically created, quickly and conveniently deployed.', NULL, NULL, 4, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (63, 'item-box', 'Quản lý dễ dàng', 'Easy to manage', 'icon icon-use', 'Giao diện hệ thống tự động, đơn giản, thân thiện giúp việc quản trị của admin trở nên dễ dàng.', 'The interface of the system is automatic, simple and friendly, making admin administration easy.', NULL, NULL, 5, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (64, 'item-box', 'Chi phí hợp lý', 'Affordability', 'icon icon-use', 'Tiết kiệm chi phí thiết lập ban đầu, chi trả chi phí hợp lý hàng tháng. Đặc biệt, doanh nghiệp có thể thay đổi mức chi trả theo như cầu sử dung thực tế.', 'Save on initial setup costs, pay reasonable monthly fees. In particular, businesses can change the payment according to actual demand.', NULL, NULL, 6, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (65, 'content-box', 'Máy chủ ảo dùng riêng Private Cloud', 'Virtual private server Private Cloud', 'uploads/admin/blog-category/20191020/5157154146720102019_page.jpg', 'Cung cấp hệ thống máy chủ ảo dùng riêng sử dụng công nghệ điện toán đám mây, giúp doanh nghiệp đảm bảo vấn đề bảo mật, độc lập về tài nguyên, tiết kiệm chi phí phần cứng và tối ưu trong việc quản trị hệ thống.', 'Providing a separate virtual server system using cloud computing technology, helping businesses ensure security, independence of resources, save hardware costs and optimize system administration.', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 03:18:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (66, 'content-box', 'Kết nối thông minh liên tục', 'Seamless intelligent connection', 'uploads/admin/blog-category/20191020/4157154202920102019_page.jpg', '<p><span style=\"color: rgb(68, 68, 68); font-family: &quot;Open Sans&quot;; font-size: 16px; background-color: rgb(252, 252, 252);\">Công cụ dữ liệu dự phòng Secondsite cung cấp giải pháp khôi phục sự cố tức khắc, nhờ khả năng sao lưu dữ liệu trên thời gian thực. Có thể phục hồi, kích hoạt và đồng bộ một phần hoặc toàn bộ dữ liệu, khả năng truy cập môi trường ảo của bạn.</span><br></p>', '<p>Secondsite backup data tools provide instant troubleshooting, thanks to real-time data backup. Can recover, activate and synchronize all or part of your data and the ability to access your virtual environment.<br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 03:27:46', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (67, 'content-box', 'Kiểm tra DR thực tế của bạn', 'Check your actual DR', 'uploads/admin/blog-category/20191020/6157154216920102019_page.jpg', '<p><span style=\"color: rgb(68, 68, 68); font-family: &quot;Open Sans&quot;; font-size: 16px; background-color: rgb(252, 252, 252);\">Triển khai chuyển đổi dữ liệu Secondsite, khắc phục sự cố nhanh chóng và hoàn toàn không ảnh hưởng tới hoạt động kinh doanh của bạn. Thậm chí khách hàng của bạn không kịp nhận ra đã xảy ra sự cố.</span><br></p>', '<p>Deploy Secondsite data conversion, fix problems quickly and completely without affecting your business activities. Even if your customers do not realize that something is wrong, deploy and convert Secondsite data, fix the problem quickly and completely without affecting your business. Your customers may not even realize that something is wrong.<br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (68, 'item-box', 'Hồi phục trong vài giây', 'Heals in seconds', 'icon icon-use', 'Với việc khôi phục dữ liệu chỉ trong 5 giây, bạn sẽ nhanh chóng khôi phục hoạt động kinh doanh của mình trong đám mây của Zettagrid.', 'With a 5-second data recovery, you will quickly resume your business in Zettagrid\'s cloud.', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (69, 'item-box', 'Quy trình nội bộ chặt chẽ', 'Tight internal process', 'icon icon-use', 'Việc phân quyền truy cập dữ liệu, giao tiếp và báo cáo nội bộ của doanh nghiệp được kiểm soát và quản lý dễ dàng trong cổng thông tin Tpcloud.', 'The decentralization of enterprise\'s data access, communication and reporting is controlled and easily managed in the Tpcloud portal.', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (70, 'content-box', 'Cáp quang nối điểm - P2P', 'Point-to-point optical fiber - P2P', 'uploads/admin/blog-category/20191020/2157154358920102019_page.png', '<p>Là dịch vụ cho thuê cáp quang trực tiếp giữa 2 địa điểm của khách hàng, Dịch vụ đáp ứng yêu cầu thiết lập kết nối trực tiếp, tốc độ cao đến 1Gbps-10Gbps của các doanh nghiệp, tổ chức với yêu cầu bảo mật tuyệt đối</p>', '<p><font color=\"#505050\" face=\"adobe-clean, Source Sans Pro, -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, sans-serif\"><span style=\"font-size: 14px; white-space: pre-wrap;\">Tpcloud offer a service of Fiber optic cable rental connecting directly between your 2 locations. The service meets the requirements of establishing directly, high-speed connection up to 1Gbps-10Gbps of businesses and organizations with absolute security requirements.</span></font><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 03:53:10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (71, 'item-box', 'Hỗ trợ kỹ thuật 14/7', 'Technical support 14/7', 'icon icon-use', 'Đường truyền được giám sát liên tục 24/7 và được hỗ trợ chuyên nghiệp bởi đội ngũ kỹ thuật giàu kinh nghiệm.', 'The connection is monitored 24/7, supported by an experienced technical team of Tpcloud.', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 16:05:02', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (72, 'item-box', 'Giám sát tự động', 'Automatic monitoring system', 'icon icon-use', '<p>Hệ thống có thể giám sát cáp quang tự động, giám sát chất lượng tuyến cáp, và tự động xác định vị trí&nbsp; đứt cáp</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Optical fiber monitoring system will  monitor the cable line quality and detect the position of cable occurs automatically </span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (73, 'item-box', 'Bảo mật tuyệt đối', 'Completely secured', 'icon icon-use', '<p>Bảo mật cao với mạng lưới cáp riêng biệt, trực tiếp, thông qua thiết bị của nhà cung cấp dịch vụ. Hỗ trợ băng thông 100Mbps, 1Gbps, 10Gbps.&nbsp;</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">High security due to the use of dedicated cable, directly, not through the equipment of the  third service provider.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (74, 'content-box', 'Cấp phép Office 365', 'Free License for Office 365', 'uploads/admin/blog-category/20191020/8157154473020102019_page.png', '<p>Sử dụng giấy phép đăng ký Office 365 của bạn trên tiền đề và trong Zettagrid mà không mất thêm chi phí.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Your can use Office 365 subscription with full licenses on premise and in Tpcloud at no additional cost.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (75, 'item-box', 'Không cần đầu tư phần cứng ban đầu', 'Using your Existing Licensing', 'icon icon-use', '<p>Office 365 được cung cấp như 1 dịch vụ thuê bao hàng tháng trên nền điện toán đám mây . Khách hàng không cần trả trước cho việc đầu tư mua mới/ nâng cấp phần cứng, tiết kiệm chci phí điện năng, chi phí vận hành.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Leverage Existing Licensing As a Microsoft Qualified Muti-Tenant Hoster (QMTH),&nbsp;Tpcoud&nbsp;can host Office 365 Pro Plus,&nbsp;Office 365 Project Professional and Office 365 Visio Professional using your existing licensing.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (76, 'item-box', 'Truy cập mọi lúc mọi nơi , trên nhiều thiết bị', 'Flexible Using', 'icon icon-use', '<p>Cho phép người dùng dù ở bất cứ nơi đâu, bất cứ lúc nào, dù  trực tuyến hay ngoại tuyến bạn đều có thể làm việc với phiên bản mới nhất của tài liệu và được thực hiện trên hầu hết mọi thiết bị : máy tính, máy tính bảng, điện thoại di động.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \" source=\"\" sans=\"\" pro\",=\"\" -apple-system,=\"\" blinkmacsystemfont,=\"\" \"segoe=\"\" ui\",=\"\" roboto,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(244,=\"\" 244,=\"\" 244);\"=\"\">You can access and use your documents offline or online whenever you need. Whatever devices you use, your Office 365 Licensing are all supports.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 04:24:26', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (77, 'item-box', 'Luôn update bản mới nhất', 'Continuously updated', 'icon icon-use', '<p>Office 365 luôn luôn được cập nhật phiên bản mới nhất , không cần đến những bản vá lỗi hay nâng cấp của phần mềm Mícrosoft sẽ liên tục bổ sung các chức năng mới mà khách hàng không cần phải trả thêm bất kì chi phí nào.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud will keep you Office 365 Licensing updated. All document you make are used the latest version with full functions and tools from Microsoft.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (78, 'item-box', 'Quản lý dữ liệu theo nhu cầu', 'Data Center based on your demand', 'icon icon-use', '<p>Bạn có quyền truy cập đầy đủ tài nguyên và công cụ để sử dụng, quản lý dữ liệu đám mây riêng tư hoặc bảo mật.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">To keep your company data secured, Tpcloud empower you to access data center, control your cloud with full tools and resources you may need.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (79, 'item-box', 'Lưu trữ, sao lưu và DR', 'Backup, storage and DR', 'icon icon-use', '<p>Dữ liệu của bạn sẽ được sao lưu và bảo mật tại máy chủ TPcloud của bạn như một bản dự phòng bởi dịch vụ sao lưu của chúng tôi.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Tpcloud will keep all your company data safe by backup tool, storage system and DR solutions.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (80, 'item-box', 'Mạng & đồng địa điểm', 'Network & Location sharing', 'icon icon-use', '<p>Thiết lập kết nối Ethernet trực tiếp, với lưu lượng miễn phí giữa các văn phòng của bạn và trung tâm dữ liệu của chúng tôi</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud offer the services of internal network and connection between your different offices with sharing the same server and data center.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (81, 'content-box', 'Bảo mật và toàn diện', 'Vietnamese full secure Cloud service', 'uploads/admin/blog-category/20191020/7157154816820102019_page.jpg', '<p>TPcloud đáp ứng nhu cầu lưu trữ dữ liệu và đưa ra gói giải pháp cho mối quan tâm hàng đầu của chính phủ về bảo mật. Bất cứ lúc nào , ở đâu, dữ liệu đám mây của tổ chức luôn được đảm bảo an toàn, bảo mật và sẵn sàng được sử dụng. </p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">The Information is very important. And it much more important to Government. To cover all concerns, Tpcloud offers the completely safe and secured Cloud service with full support from professional team and world leading technology system from VMware.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 05:09:40', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (82, 'item-box', 'Giải pháp địa phương hóa', 'Localised Cloud Solutions', 'icon icon-use', '<p>Chúng tôi làm việc với đối tác địa chương của chúng tôi, những người có kinh nghiệm cung cấp giải pháp CNTT trong nhiều tổ chức chính phủ.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">TPcloud and local partners, together, we support Government Cloud and IT solutions right in your location with all our best.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (83, 'item-box', 'Chủ quyền dữ liệu', 'Data Sovereignty Guarantee', 'icon icon-use', '<p>Tất cả cơ sở hạ tầng và dữ liệu khách hàng của chúng tôi được đặt an toàn trong trung tâm dữ liệu của Việt Nam.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Besides keeping your data safe and secured, Tpcloud also make sure that all your cloud and systems will be protected within Vietnam and your state.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (84, 'item-box', 'Tiết kiệm chi phí', 'Optimized Cost', 'icon icon-use', '<p>Không còn áp lực bỏ vốn lớn cho đầu tư ban đầu cho phần cứng hay duy trì đội ngũ kỹ thuật hỗ trợ.TPcloud giải quyết bài toán bằng tối ưu chi phí.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">You can plan about a cloud services with the surprised reasonable budget. Because Tpcloud offers the best solutions with affordable cost based on your needs.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (85, 'content-box', 'Giáo dục thông minh trên nền tảng công nghệ đám mây', 'Smart education on cloud technology platform', 'uploads/admin/blog-category/20191020/0157154932720102019_page.jpg', '<p>TPcloud cung cấp một giải pháp đám mây toàn diện cho các tổ chức giáo dục, giúp giáo viên cơ hội kết nối lưu trữ một cách an toàn, hỗ trợ việc học tập và kết nối cho những học sinh , sinh viên của mình.</p>', '<p>TPcloud provides a comprehensive cloud solution for educational institutions, giving teachers the opportunity to securely connect storage, support learning and connect for their students.<br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (86, 'item-box', 'Thiết lập và triển khai đơn giản', 'Super Fast Installation', 'icon icon-use', '<p>Với vài thao tác đơn giản, tổ chức có thể nhanh chóng truy cập vào đám mây của mình trên TPcloud,&nbsp; lưu trữ, chia sẽ dữ liệu và được đảm bảo mọi yếu tố an toàn bảo mật.&nbsp;</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Within some minutes with clicks, your data can be saved, transfer without waiting. All made in your Public cloud.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (87, 'item-box', 'Sao lưu, bảo mật và khôi phục dữ liệu', 'Backup, storage and DR', 'icon icon-use', '<p>Dữ liệu của khách hàng sẽ luôn được lưu giữ một bản sao dự phòng. Khách hàng có thể truy cập vào nguồn dữ liệu này thông qua dịch vụ máy chủ nhân rộng mà TPcloud cung cấp.</p>', '<p>Customer data will always be kept a backup copy. Customers can access this data source through the replication server service provided by TPcloud.<br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (88, 'content-box', 'Phục hồi dữ liệu dễ dàng', 'Cloud Data Recovery easily', 'uploads/admin/blog-category/20191020/6157154999420102019_page.jpg', '<p>Dữ liệu của bạn trên TPcloud sẽ luôn được sao lưu thành một bản dự phòng và được khôi phục nhanh chóng khi xảy ra sự cố. Đám mây của bạn sẽ luôn được phục hồi dữ liệu dễ dàng, khả năng chịu lỗi và tính sẵn sàng cao.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud will always make a backup of your data and keep it safe and secured. Whenever disaster occurs, this backup is available to  recover your data promptly. This tool allows your cloud Easy data recovery, fault tolerance, high availability.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 05:39:55', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (89, 'item-box', 'Giảm gián đoạn hoạt động kinh doanh', 'Recover business faster', 'icon icon-use', 'Hạ tầng công nghệ VMware cho phép phục hồi các sự cố về dữ liệu một cách nhanh chóng, kịp thời, giảm thiểu ảnh hưởng tới hoạt động kinh doanh.', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Cryptolocker, viruses, software bugs, patching, project upgrades can be disaster to your business if they make your data system stuck or down. Tpcloud will great tools can cover your concerns about this.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (90, 'item-box', 'Bảo mật và an toàn dữ liệu', 'Keep Data safe and secured', 'icon icon-use', 'Hạ tầng công nghệ VMware cho phép bạn truy cập linh hoạt vào dữ liệu đám mây , kiểm soát và quản lý cloud và máy chủ ảo ở mức cao nhất. Đảm bảo dữ liệu của bạn luôn an toàn tại TPcloud', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">All your concerns about keeping you data safe and secured, Tpcloud offers the solutions can completely cover them with VMware environment and Partners.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (91, 'item-box', 'Tiết kiệm chi phí khắc phục', 'Saving issue handling cost', 'icon icon-use', '<p>Với đội ngũ và dịch vụ hỗ trợ của TPcloud, bạn không cần phải tiêu tốn kinh phí cho các chuyên gia bên ngoài khi dữ liệu gặp sự cố.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">You will not worry about costly maintenance team or IT facilities in-house. Let Tpcloud cover all your issues with professional team and affordable cost.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (92, 'content-box', 'Ứng dụng truyển khai nhanh trên Host TPcloud', 'Fast installation and running in Tpcloud host', 'uploads/admin/blog-category/20191020/6157155075520102019_page.jpg', '<p>Không cần thao tác phúc tạp, ngay khi Host TPcloud tiếp nhận ứng dụng của bạn, ứng dụng có thể hoạt động triển khai hoạt động ngay khi sẵn sàng. Và, bạn hoàn toàn quản lý cơ sở hạ tầng theo cách tối ưu nhất.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Just by some simple and quick click, you games, websites, apps can run and open your business when you need.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 05:52:35', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (93, 'item-box', 'An toàn dữ liệu', 'Data Secure', 'icon icon-use', '<p>TPcloud Hosting đảm bảo cho dữ liệu App của bạn được bảo mật và an toàn cấp doanh nghiệp với 3 cấp độ: bảo mật điện tử , bảo mật cơ học, cách ly máy chủ &amp; lưu trữ.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcould Hosting guarantee about safety and secure for your data with 3 level off security: electronic security, physical security, network separation, and server &amp; storage isolation.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (94, 'item-box', 'Kiểm soát lưu lượng', 'Controllable Scale', 'icon icon-use', '<p>Bạn có thể chia tỷ lệ liền mạch để xử lý biến động nhanh chóng về số lượng người dùng bằng cách sử dụng tài khoản của tôi để quản lý tài nguyên hệ thống của bạn bất cứ lúc nào trong ngày hay đêm.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Your won’t get bill shock from an unexpected peak of fluctuations in numbers of users. Tpcloud \"My account Portal\" totally allows you control your user traffic.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (95, 'item-box', 'Tối ưu chi phí', 'Best cost control', 'icon icon-use', '<p>Bạn tiết kiệm được khoản chi phí lớn đầu tư ban đầu cho thiết lập cơ sở hạ tầng. Thay vào đó, bạn chỉ phải chi trả cho phần phí hoạt động và lưu lượng sử dụng thực tế.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">You can save the fee for first investment of IT facilities. And, you just need to pay the cost for real operation and actual use.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (96, 'profile', 'Tăng tính khả thi dự án của bạn', 'Make you business ability', 'uploads/admin/blog-category/20191020/3157155176120102019_page.jpg', '<p>Vốn đầu tư  ban đầu sẽ quyết định tính khả thi và thành công của dự án. TPcloud cung cấp cho bạn giải pháp đúng với nhu cầu của dự án tại thời điểm hiện tại và tiềm năng mở rộng dự an trong tương lại.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">Too much cost at the begin can prevent your project go further. Let Tpcloud share with you the concern.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 06:09:22', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (97, 'item-box', 'Giảm \"rào cản\" cho dự án', 'Reduce obstacles for project', 'icon icon-use', '<p>Khi dự án đi vào giai đoạn gấp rút, My Account của TPcloud sẽ hỗ trợ bạn bằng Seft Service portal,&nbsp; giảm tối đa cho bạn những đình trệ và rút ngắn thời gian thực hiện.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">My Account Self Service of Tpcloud portal helps shorten time for your project implementation and decommissioning.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (98, 'item-box', 'Đáp ứng việc thay đổi nhu cầu.', 'Demand can be customized', 'icon icon-use', '<p>Nhận thấy dịch vụ vượt quá khả năng bạn có thể phụ trách , TPcloud sẽ đáp ứng các thay đổi về nhu cầu dữ liệu bạn yêu cầu.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Based on your team capacity and project scale, processing, Tpcloud open with your changes to match your actual needs.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (99, 'item-box', 'Chi phí minh bạch', 'Transparent cost', 'icon icon-use', '<p>TPcloud cung cấp giá cố định cho các dịch vụ đám mây của chúng tôi và bạn sẽ không bao giờ bị sốc hóa đơn vào cuối tháng.</p>', '<p>TPcloud offers a fixed price for our cloud services and you will never have a bill shock at the end of the month.<br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (100, 'content-box', 'Bảo vệ dữ liệu thông minh', 'Smart protection', 'uploads/admin/blog-category/20191020/3157155188320102019_page.jpg', '<p>TPcloud cung cấp giải pháp lưu trữ thông minh cho dữ liệu ngày càng lớn của doanh nghiệp.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Your Data will get more and bigger. The data protection and management may become challenge. Tpcloud data storage system will sopport you well to cover this.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (101, 'item-box', 'Lưu trữ, chuyển giao dữ liệu nhanh hơn.', 'Save and transfer your Data faster', 'icon icon-use', '<p>TPcloud cung cấp \"nhà kho\" vô hạn cho dữ liệu lưu trữ của doanh nghiệp</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">You want a big or huge storage for you data, let TPcloud share you the solutions. Your company may need 1Tb or much more, we can support.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (103, 'item-box', 'An toàn ưu tiên hàng đầu', 'Data safe and secured First', 'icon icon-use', '<p>Dữ liệu của bạn luôn được kiểm soát trong suốt quá trình xử lý. TPcloud hỗ trợ sao lưu tự động và khắc phục sự cố tức khắc đảm bảo dữ liệu của bạn luôn an toàn.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud platform with firewall and backup &amp; recover system will keep your data safe and secured during processing and storage.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (104, 'profile', 'Corporate Profile & History', 'En Corporate Profile', 'uploads/admin/blog-category/20191020/6157155245420102019_page.jpg', '<ol style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 2em; padding: 0px; font-size: 14px; font-family: Meiryo, \"Hiragino Kaku Gothic ProN\", sans-serif;\"><li style=\"margin: 0px; padding: 0px;\">Design and construction of heating, ventilation, air conditioning (HVAC) systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of water supply, drainage and water filtration systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of disaster-prevention systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of clean rooms</li><li style=\"margin: 0px; padding: 0px;\">Development and application of our original “RiCS” system</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of cooling and heating systems and snow melting systems</li><li style=\"margin: 0px; padding: 0px;\">Construction of photovoltaic power plants</li></ol>', '<ol style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 2em; padding: 0px; font-size: 14px; font-family: Meiryo, &quot;Hiragino Kaku Gothic ProN&quot;, sans-serif;\"><li style=\"margin: 0px; padding: 0px;\">Design and construction of heating, ventilation, air conditioning (HVAC) systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of water supply, drainage and water filtration systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of disaster-prevention systems</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of clean rooms</li><li style=\"margin: 0px; padding: 0px;\">Development and application of our original “RiCS” system</li><li style=\"margin: 0px; padding: 0px;\">Design and construction of cooling and heating systems and snow melting systems</li><li style=\"margin: 0px; padding: 0px;\">Construction of photovoltaic power plants</li></ol>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-29 03:05:43', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (106, 'profile', 'Lưu trữ SaaS', 'SaaS hosting', 'icon icon-use', '<p>TPcloud cung cấp quyền truy cập trực tiếp vào bộ dụng cụ VMware, cho phép cấp độ quản lý cao nhất.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">SaaS hosting of Tpcloud helps you can retain high levels of visibility and control data and system, even when you have an Application serving thousand of users.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 06:26:05', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (107, 'item-box', 'DMZ doanh nghiệp', 'DMZ for corporate', 'icon icon-use', '<p>TPcloud cung cấp giải pháp an toàn cho dữ liệu doanh nghiệp tránh khỏi các cuộc tấn công nguy hại qua internet, đảm bảo dữ liệu và hệ thống của bạn luôn an toàn.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud DMZ will prevent your corporate cloud from number of malicious attacks. Your corporate network will be safe and you can  mitigate risks which come from outside application installations.</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (108, 'profile', 'Lời chào', 'Greeting', 'uploads/admin/blog-category/20191020/4157155352520102019_page.jpg', '<p><font style=\"margin: 0px; padding: 0px; font-size: 14px; font-family: Meiryo, &quot;Hiragino Kaku Gothic ProN&quot;, sans-serif; vertical-align: inherit;\">Chúng tôi đang làm việc để tạo ra một môi trường thoải mái bằng cách thêm cuộc sống vào các cơ sở chúng tôi đã tạo ra.&nbsp;</font><font style=\"margin: 0px; padding: 0px; font-size: 14px; font-family: Meiryo, &quot;Hiragino Kaku Gothic ProN&quot;, sans-serif; vertical-align: inherit;\">Các biện pháp chúng tôi thực hiện phải phù hợp với chính sách trân trọng và giữ gìn môi trường toàn cầu quý giá của chúng tôi.</font><br></p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud offer you Private Cloud solutions, optimized your IT investment with enterprise scale environment including data center resiliency, security and process management.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-29 03:06:56', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (109, 'item-box', 'Bỏa vệ dữ liệu quí giá', 'Protect your important assets', 'icon icon-use', '<p>Dữ liệu doanh nghiệp của bạn sẽ được lưu trữ bảo mật trên đám mây TPcloud. Hồ sơ nhân viên, thông tin khách hàng, dữ liệu sản phẩm, .. tất cả đều được chúng tôi đảm bảo an toàn.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">All your important information and data such as Staff records, finance reports, Client contacts...will be safe and secured by VMware system and Tpcloud services.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (110, 'item-box', 'Hỗ trợ hiệu quả', 'Maximum Supports', 'icon icon-use', '<p>Trung tâm cơ sở hạ tầng CNTT của bạn trong TPcloud, bạn sẽ không cần nhân viên dành thời gian cho việc bảo trì và sửa chữa thiết bị máy chủ.</p>', '<span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">You will optimize your cost and attention when all IT system located within Tpcloud platform.  Less time spending , Less cost paying, More efficiencies.</span>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (111, 'item-box', 'Gắn kết nhân viên', 'Employee Engagement', 'icon icon-use', '<p>Cho đổi dễ dàng qua mạng nội bộ chung, doanh nghiệp vừa loại bỏ phụ thuộc vào các công cụ giao tiếp bên ngoài vừa gắn kết công đồng nhận viên trong công ty.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Private Cloud of Tpcloud platform reduce dependence and keep your business more effective thank to faster access, your team more free to engage to their tasks, .</span><br></p>', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (112, 'profile', 'Mở rộng môi trường dữ liệu doanh nghiệp', 'Open up your corporate cloud', 'uploads/admin/blog-category/20191020/6157155422020102019_page.jpg', '<p>Mở rộng dữ liệu đám mây của bạn bằng hệ thống phân cấp thành nhiều đám mây khác nhau trên cùng hạ tầng VMware</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">Tpcloud offers you Hybrid Cloud solutions for You can have the same VMware cloud environment with muti-levels of data cloud.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, '2019-10-20 06:50:21', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (113, 'item-box', 'Đẩy nhanh dự án', 'Boost your business', 'icon icon-use', '<p>Cho phép các dự án xây dựng một \"môi trường dữ liệu riêng\" , sau đó có thể cho môi trường đó ngưng hoạt động chỉ trong vài giờ hoặc trong vài ngày thay vì phải mất vài tuần hay vài tháng như trước đây.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">With our Hybrid Cloud solutions, you don\'t need months or years to launch a project with complex setup and installation. Even when you want to temporary shut it down, after that you can re-load it fast and easily.</span><br></p>', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (114, 'item-box', 'Đồng bộ hạ tầng công nghệ thông tin', 'IT system Consistency', 'icon icon-use', '<p>Hybird cloud của TPcloud tạo tính nhất quán, đồng bộ tất cả chính sách bảo mật trong môi trường đám mây song song với chính sách thực tế về dữ liệu tại doanh nghiệp bạn.</p>', '<p><span style=\"color: rgb(80, 80, 80); font-family: adobe-clean, &quot;Source Sans Pro&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, sans-serif; font-size: 14px; white-space: pre-wrap;\">We will apply the same security policies and infrastructure builds as exist in your corporate environment. Ensuring your IT complexity and support overheads remain fixed.</span><br></p>', NULL, NULL, 2, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (116, 'item-box', 'Tích hợp đầy đủ Veeam', 'Fully integrated Veeam', 'icon icon-use', '<p>Tpcloud và công cụ Veeam cung cấp giải pháp sao lưu dữ liệu ngoại vi đồng thời với công cụ Veeam Av Available Suite cho đám mây riêng tại chỗ.<br></p>', '<p>Tpcloud and Veeam tools provide simultaneous offsite data backup solution with Veeam Av Available Suite for on-premises private cloud.<br></p>', NULL, NULL, 11, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (117, 'item-box', 'Thiết lập trong vài phút', 'Set up in minutes', 'icon icon-use', '<p>Sau khi đăng nhập Tpcloud, bạn chỉ tốn vài phút để thiết lập để sử dụng đám mây của mình. Đồng thời, bạn được tuỳ chỉnh dung lượng, sao lưu dữ liệu bất kỳ lúc nào.<br></p>', '<p>After logging in to Tpcloud, it only takes a few minutes to set up to use your cloud. At the same time, you can customize the capacity, backup data at any time.<br></p>', NULL, NULL, 12, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tpcloud_cms_plugin` VALUES (118, 'item-box', 'Không có phí chuyển dữ liệu', 'There is no data transfer charge', 'icon icon-use', '<p>Sử dụng Tpcloud cùng công cụ sao lưu Veeam, dù bạn muốn sao lưu nhiều đến đâu cũng không mất thêm chi phí nào để luân chuyển dữ liệu.<br></p>', '<p>Using Tpcloud with Veeam backup tool, no matter how much you want to back up, there is no additional cost to transfer data.<br></p>', NULL, NULL, 13, NULL, NULL, NULL, NULL, NULL, 1, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
